from fastapi import FastAPI, HTTPExceptionfrom .blockchainAPI import BlockChainapp = FastAPI()blockchain = BlockChain()@app.on_event("startup")async def startup_event():    blockchain.connect()@app.get("/")async def root():    if not blockchain.is_connected():        blockchain.connect()        if not blockchain.is_connected():            raise HTTPException(status_code=402, detail="Couldn't connect to blockchain")            res = blockchain.getRes()    return res